sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast:function(strDate){
		//get current date
		var rightNow = new GlideDateTime();
		//get the string date
		var testedTime = new GlideDateTime(strDate);

		//if the testedTime is before right now then return ture, otherwise return false
		if(testedTime.before(rightNow)){
			return true;
		}else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
