sysauto_script.script:
//Get current time
var currentTime = new GlideDateTime();

//count 24 hours to add
var nextDay = new GlideDateTime();
nextDay.addDaysLocalTime(1);

//Query the records whose due date is between now and 24 hours from now
var upcomingNITask = new GlideRecord('x_58872_needit_needit_task');
upcomingNITask.addQuery('due_date','>=', currentTime);

upcomingNITask.addQuery('due_date', '<=', nextDay);

upcomingNITask.query();

while(upcomingNITask.next()){
	gs.info("Upcoming tasks record: " + upcomingNITask.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTasks", upcomingNITask, upcomingNITask.number, gs.getUserName());
}
